function X=generate_artificial(repetition,D,number,d,outliernumber,noise)for i=1:repetitiontrue_direction{i}=orth(rand(D,d));X{i}=randn(number,d)*true_direction{i}';X{i}=[X{i};rand(outliernumber,D)];X{i}=X{i}+rand(size(X{i}))*noise;end%save([num2str(D),'_',num2str(number),'_',num2str(d),'_',num2str(outliernumber),'_',num2str(noise),'uniform','.mat'],'X','true_direction');%a=1;